//variables

$primary-desaturated-red: hsl(0, 36%, 70%);
$primary-soft-red: hsl(0, 93%, 68%);
$neutral-grayish-red: hsl(0, 6%, 24%);

$primary-ff: "Josefin Sans", sans-serif; /* font-weight 300,400,600 */

///////////////////
////////////// breakpoints

$small: 28em;
$medium: 37.9em;
$medium-big: 50em;
$large: 59.37em;
$larger: 75em;
///////////////////
//////////////mixin
@mixin media($size) {
  @media screen and (min-width: $size) {
    @content;
  }
}

//////////////////////////////
///////////////////

html {
  font-size: 62.5%;
}

*,
*::before,
*::after {
  margin: 0;
  padding: 0;
  box-sizing: inherit;
}

body {
  box-sizing: border-box;
  font-family: $primary-ff;
  display: grid;
}

.container {
  position: relative;

  background-image: url(../images/bg-pattern-desktop.svg);
  margin: auto;
  box-shadow: -1px 0 25px -15px #000;
  border-radius: 5px;
  @include media($large) {
    & {
      max-width: 144rem;
      margin: auto;
      margin-top: 7rem;
    }
  }

  &-box {
    @include media($large) {
      & {
        display: flex;
        flex-direction: row-reverse;
        align-items: center;
      }
    }
  }
}

////////////////////
///////////nav-logo
/* nav-logo */

@include media($large) {
  .nav__logo {
    position: absolute;
    padding-left: 7rem;
  }
}

.nav__heading {
  padding: 4rem 0rem 4.5rem 6.7rem;
  font-size: 1.3rem;
  text-transform: uppercase;
  letter-spacing: 2.5px;
  line-height: 1.3;
  position: relative;

  &::before {
    content: "";
    position: absolute;
    top: 4.3rem;
    left: 3.5rem;
    height: 2.5rem;
    width: 2.5rem;
    border: 3.5px solid transparent;
    border-radius: 50px;
    background-image: linear-gradient(hsl(0, 0%, 100%), hsl(0, 100%, 98%)),
      linear-gradient(to right, hsl(0, 80%, 86%), hsl(0, 74%, 74%));
    background-origin: border-box;
    background-clip: content-box, border-box;
  }

  @include media($medium-big) {
    & {
      font-size: 2.5rem;
      display: inline-block;
      padding-left: 8.1rem;
      padding-top: 5.5rem;
    }
    &::before {
      height: 3.5rem;
      width: 3.5rem;
      top: 6.7rem;
    }
  }

  &-span {
    display: block;
  }
}

/////////////////////////
///////////////Header
/* header */

.header {
  width: 100%;

  &__image {
    width: 100%;
    display: block;
  }
  @include media($medium-big) {
    &__image {
      height: 70rem;
      object-fit: cover;
      object-position: top center;
    }
  }

  @include media($larger) {
    &__image {
      height: 100%;
    }
  }
}

//////////////////////////////
//////////////////main
/* main */

.main {
  @include media($large) {
    & {
      width: 60%;
      padding: 0rem 10rem 0 10.5rem;
    }
  }
  @include media($larger) {
    & {
      padding-right: 21rem;
    }
  }
}

///////////////////////
/////////////////main-section
/* main-section */
.section {
  padding: 0 2.5rem;

  @include media($small) {
    & {
      padding: 0 4.5rem;
    }
  }
  @include media($medium) {
    & {
      padding: 0 8.5rem;
    }
  }
  @include media($medium-big) {
    & {
      padding: 0 15rem;
    }
  }
  @include media($large) {
    & {
      padding: 0;
    }
  }

  &__heading {
    padding-top: 5.5rem;
    padding-bottom: 3.5rem;
    font-size: 3.5rem;
    text-transform: uppercase;
    font-weight: 300;
    letter-spacing: 1rem;
    color: $primary-desaturated-red;
    text-align: center;

    @include media($small) {
      & {
        font-size: 5rem;
      }
    }
    @include media($medium-big) {
      & {
        font-size: 5.5rem;
        line-height: 1.1;
        padding-bottom: 3.5rem;
      }
    }
    @include media($large) {
      & {
        padding-top: 7rem;
        line-height: 1.25;
        text-align: left;
        padding: 9rem 25rem 2.5rem 0;
      }
    }
  }

  &__span {
    display: block;
    font-weight: 600;
    color: #000;
    @include media($large) {
      & {
        display: block;
        text-align: left;
      }
    }
  }

  &__text {
    font-size: 1.7rem;
    letter-spacing: 1px;
    line-height: 1.5;
    text-align: center;
    padding-bottom: 4.5rem;
    color: $primary-desaturated-red;

    @include media($small) {
      & {
        font-size: 2rem;
      }
    }

    @include media($medium-big) {
      & {
        font-size: 2.3rem;
      }
    }

    @include media($large) {
      & {
        font-size: 2rem;
        text-align: left;
      }
    }
  }

  /////////////////////////////
  ///////////////////section-form
  /* section-form */

  &__form {
    position: relative;
    margin-bottom: 5rem;

    @include media($medium-big) {
      & {
        margin-bottom: 9.5rem;
      }
    }

    @include media($large) {
      & {
        margin-bottom: 0;
      }
    }

    &-input {
      height: 5rem;
      width: 100%;

      border: 1.5px solid $primary-desaturated-red;
      border-radius: 25px;
      outline: none;
      text-indent: 25px;

      &::placeholder {
        letter-spacing: 0.5px;
        color: $primary-desaturated-red;
      }
    }
    &-btn {
      position: absolute;
      right: 0;
      top: 0;
      width: 7.5rem;
      height: 100%;
      font-size: 2.5rem;
      border: none;
      background: linear-gradient(135deg, hsl(0, 80%, 86%), hsl(0, 74%, 74%));
      border-radius: 25px;
      cursor: pointer;
      transition: all 0.3s;

      &:hover {
        background: linear-gradient(135deg, hsl(0, 58%, 84%), hsl(0, 48%, 83%));
      }
      &::after {
        background: url(../images/icon-arrow.svg);
        width: 1.5rem;
        height: 1.5rem;
      }
    }
    &-arrow {
      position: absolute;
      top: 1.5rem;
      right: 3rem;
      color: #000;
    }
    &-error {
      position: absolute;
      top: 1.3rem;
      right: 8.5rem;
      opacity: 0;
      transition: opacity 0.2s;
    }
    &-label {
      position: absolute;
      top: 6rem;
      left: 2.3rem;
      font-size: 1.3rem;
      color: $primary-desaturated-red;
      opacity: 0;
      transition: opacity 0.2s;
    }
  }
}

/////////////////////
///////////////////javascript active state of the form input//////
/*javascript active state of the form input  */

.active {
  opacity: 1;
}

//////////////////////////////
/////////////////Footer
/* Footer */

.footer {
  margin-top: 5rem;
  &__attribution {
    font-size: 11px;
    text-align: center;
  }
  &__link {
    color: $primary-desaturated-red;
    font-size: 1.5rem;
  }
}
